// Check if we have failing feeds to test
const data = $input.first().json;
const feeds = data.feeds || [];

if (feeds.length === 0) {
  return [{ json: { 
    _route: 'no_feeds', 
    status: 'no_failing_feeds',
    message: 'No failing feeds to check',
    timestamp: new Date().toISOString()
  } }];
}

// Return feeds for individual testing
return feeds.map(feed => ({ 
  json: { 
    ...feed,
    _route: 'test_feed'
  } 
}));

// Analyze test results and prepare updates
const items = $input.all();
const results = [];
const toRecover = [];
const stillFailing = [];

for (const item of items) {
  const data = item.json;

  // Skip if this was the 'no feeds' path
  if (data._route === 'no_feeds') {
    return [{ json: data }];
  }

  const statusCode = data.statusCode || 0;
  const isSuccess = statusCode >= 200 && statusCode < 400;

  const result = {
    feedId: data.id,
    feedName: data.name,
    feedUrl: data.url,
    testResult: isSuccess ? 'recovered' : 'still_failing',
    statusCode: statusCode,
    testedAt: new Date().toISOString()
  };

  results.push(result);

  if (isSuccess) {
    toRecover.push(result);
  } else {
    stillFailing.push(result);
  }
}

return [{
  json: {
    _route: 'results',
    results: results,
    toRecover: toRecover,
    stillFailing: stillFailing,
    summary: {
      total: results.length,
      recovered: toRecover.length,
      stillFailing: stillFailing.length
    }
  }
}];

// Update recovered feeds back to active status
const input = $input.first().json;

if (input._route === 'no_feeds') {
  return [{ json: input }];
}

const toRecover = input.toRecover || [];
const apiUrl = $env.EMBER_API_URL || 'http://host.docker.internal:3002';

// Note: In production, we'd make PATCH calls here to update feed status
// For now, we'll prepare the update payloads
const updates = toRecover.map(feed => ({
  feedId: feed.feedId,
  update: {
    status: 'active',
    consecutiveFailures: 0,
    lastHealthCheck: new Date().toISOString()
  }
}));

return [{
  json: {
    ...input,
    updatesApplied: updates.length,
    updates: updates
  }
}];

// Prepare final summary report
const input = $input.first().json;

if (input._route === 'no_feeds') {
  return [{ json: {
    status: 'complete',
    message: input.message,
    feedsChecked: 0,
    recovered: 0,
    stillFailing: 0,
    timestamp: input.timestamp
  } }];
}

const summary = input.summary || { total: 0, recovered: 0, stillFailing: 0 };

return [{
  json: {
    status: 'complete',
    feedsChecked: summary.total,
    recovered: summary.recovered,
    stillFailing: summary.stillFailing,
    updatesApplied: input.updatesApplied || 0,
    details: input.results || [],
    timestamp: new Date().toISOString(),
    executionId: $execution.id
  }
}];
