// Check if we have failing feeds
const data = $input.first().json;
const feeds = data.feeds || [];

if (feeds.length === 0) {
  return [{ json: { status: 'no_failing_feeds', message: 'No failing feeds to check' } }];
}

// Return feeds for testing
return feeds.map(feed => ({ json: feed }));

// Analyze test results
const items = $input.all();
const results = [];

for (const item of items) {
  const statusCode = item.json.statusCode || 0;
  const isSuccess = statusCode >= 200 && statusCode < 400;

  results.push({
    feedId: item.json.id || 'unknown',
    feedUrl: item.json.url || 'unknown',
    testResult: isSuccess ? 'recovered' : 'still_failing',
    statusCode: statusCode
  });
}

const recovered = results.filter(r => r.testResult === 'recovered').length;
const stillFailing = results.filter(r => r.testResult === 'still_failing').length;

return [{
  json: {
    status: 'complete',
    feedsChecked: results.length,
    recovered: recovered,
    stillFailing: stillFailing,
    results: results
  }
}];
